function ListGroup() {
  let items = [
    "An item",
    "A second item",
    "A third item",
    "A fourth item",
    "And a fifth one",
  ];
  items = []; // Uncomment this line to test the empty state
  if(items.length === 0) return <><h1>List</h1><p>No items found</p></>;
  return (
    //Simple Syntax for Fragment from React
    <>
      <h1>List</h1>
      <ul className="list-group">
        {items.map((item) => (
          <li key={item}>{item}</li>
        ))}
      </ul>
    </>
  );
}

export default ListGroup;
